    // Set a value in cell located at 4th row. Indexing starts at 0 for row\'s cells.\r\n        row.Cells[ 1 ].Value = "Cell value for 2nd cell of RowId 3";\r\n        row.Cells[ "D" ].Value = "Cell value for cell in column \'D\' of RowId 3";\r\n\r\n        // Set AutoFit for columns with values.\r\n        worksheet.Columns[ 1 ].AutoFit();\r\n        worksheet.Columns[ "D" ].AutoFit();\r\n\r\n        // Making sure only 2 cells in the 4th row exists (the modified cells).\r\n        Debug.Assert( row.Cells.Count == 2 );\r\n\r\n        // Save workbook to disk.\r\n        workbook.Save();\r\n        Console.WriteLine( "\\tCreated: RowCellAccess.xlsx\\n" );\r\n      }\r\n    }\r\n\n'})})]})}function w(e={}){const{wrapper:o}={...(0,t.R)(),...e.components};return o?(0,s.jsx)(o,{...e,children:(0,s.jsx)(d,{...e})}):d(e)}},28453:(e,o,r)=>{r.d(o,{R:()=>c,x:()=>l});var s=r(96540);const t={},n=s.createContext(t);function c(e){const o=s.useContext(n);return s.useMemo((function(){return"function"==typeof e?e(o):{...o,...e}}),[o,e])}function l(e){let o;return o=e.disableParentContext?"function"==typeof e.components?e.components(t):e.components||t:c(e.components),s.createElement(n.Provider,{value:o},e.children)}}}]);